#!/bin/sh

libfile=$1
libdir=`dirname "$libfile"`
MYOLDPWD=$OLDPWD
cd "$libdir"
libdir=`pwd`
cd -
OLDPWD=$MYOLDPWD
libname=`basename "$libfile" .la`

cat > "$libdir/$libname.la" << __EOF__
# $libname.la - a libtool library file
# Generated by libtool
# (Not really, but the validity check wants that line.)

dlname=''
library_names='$libname.a'
old_library='$libname.a'
dependency_libs=''
current=0
age=0
revision=1
installed=no
shouldnotlink=no
dlopen=''
dlpreopen=''
libdir='$libdir'
__EOF__
